// Get the string from previous node
let raw = $input.first().json.output;

// If it's a string, parse it to JSON
let data = typeof raw === 'string' ? JSON.parse(raw) : raw;

// Build HTML for each item
let orderHTML = (data.orderItems || [])
  .map(item => `
    <p style="margin: 4px 0; font-size: 14px;">
      ${item.name} √ó ${item.quantity} ‚Äì ${data.currency}${item.price} each
    </p>
  `)
  .join('');

// Create a formatted total bill string
let totalBillText = `${data.currency}${data.totalBill} (${data.taxInfo || 'incl. all taxes'})`;

// Return both separately
return [
  {
    json: {
      orderHTML,      // all the item rows
      totalBill: totalBillText // final amount separately
    }
  }
];

